import React, { Component } from 'react';


class MovieDetail extends Component {
  state = {
    movies: []
  }

  async componentDidMount() {
    try {
      const res = await fetch(`https://api.themoviedb.org/3/movie/${this.props.match.params.id}?api_key=ccd177fb9015c5f032160d86c9f22d86&language=en-US`);
      const movies = await res.json();
      console.log(movies);
      this.setState({
        movies: movies.results
      });
    } catch(e) {
      console.log(e);
    }
  }

  render() {
    return (
        <div>
            <h1>Hello</h1>
        </div>
    );
  }
}

export default MovieDetail;
